mapper.select:
/*_boardconf : boardconf-sqlmap.xml : boardConfList */ SELECT B.* FROM ( SELECT ROWNUM NUM, A.* FROM ( SELECT A.DOMAIN_CD AS domainCd , A.BBS_CD AS bbsCd , A.BBS_NM AS bbsNm , A.BBS_DC AS bbsDc , A.USE_YN AS useYn , TO_CHAR(A.REGIST_DT, 'YYYY-MM-DD') AS registDt , (SELECT COUNT(BBSCTT_SEQ) FROM OP_BOARD WHERE DOMAIN_CD = A.DOMAIN_CD AND BBS_CD = A.BBS_CD) AS articleCnt , (SELECT COUNT(CMT_SEQ) FROM OP_NTT_CMT WHERE DOMAIN_CD = A.DOMAIN_CD AND BBS_CD = A.BBS_CD ) AS commentCnt , (SELECT COUNT(Y.FILE_ORDR_NO)S FROM OP_BOARD X, OP_FILE Y WHERE X.DOMAIN_CD = A.DOMAIN_CD AND X.BBS_CD = A.BBS_CD AND X.FILE_SEQ = Y.FILE_SEQ) AS fileCnt , B.KND_CD AS kndCd , B.CTG_YN AS ctgYn , B.NOTICE_BBSCTT_USE_YN AS noticeBbscttUseYn , B.CMT_YN AS cmtYn , B.SKIN_PATH AS skinPath , C.MNGR_EDITR_YN AS mngrEditorYn , C.USER_EDITR_YN AS userEditorYn , C.FILE_YN AS fileYn , C.BANNED_WRD_YN AS bannedWordYn , C.CAPTCHA_YN AS captchaYn , C.OTHBC_YN AS othbcYn , D.LIST_VIEW_CD AS listViewCd , D.RECOMEND_YN AS recomendYn , D.ACCUSE_YN AS accuseYn , D.STSFDG_YN AS stsfdgYn , E.DWLD_YN AS dwldYn , E.FEED_YN AS feedYn FROM OP_BOARD_CONF A, OP_BOARD_CONF_GLOBAL B, OP_BOARD_CONF_FORM C, OP_BOARD_CONF_VIEW D, OP_BOARD_CONF_LIST E WHERE A.BBS_CD = B.BBS_CD AND A.BBS_CD = C.BBS_CD AND A.BBS_CD = D.BBS_CD AND A.BBS_CD = E.BBS_CD AND A.DOMAIN_CD = B.DOMAIN_CD AND A.DOMAIN_CD = C.DOMAIN_CD AND A.DOMAIN_CD = D.DOMAIN_CD AND A.DOMAIN_CD = E.DOMAIN_CD AND A.DOMAIN_DELETE_AT = 'N'
ORDER BY ${q_sortName} ${q_sortOrder}
ORDER BY A.CONF_ORDR_NO ASC
) A WHERE ROWNUM <= #{pagingEndNum} ) B WHERE NUM >= #{pagingStartNum}

/*_boardconf : boardconf-sqlmap.xml : boardConfSimpleList */ SELECT A.DOMAIN_CD AS domainCd , A.BBS_CD AS bbsCd , A.BBS_NM AS bbsNm , A.BBS_DC AS bbsDc FROM OP_BOARD_CONF A
WHERE NOT(A.BBS_CD = #{bbsCd}) AND A.DOMAIN_CD = #{domainCd} AND A.USE_YN = 'Y'
ORDER BY A.BBS_CD ASC

