mapper.select:
/*_boardconf : boardconf-sqlmap.xml : boardDisplayColumnList */
    SELECT COLUMN_ID        AS columnId 
         , COLUMN_NM        AS columnNm
         , BEAN_NM          AS beanNm
         , LANG_COLUMN_NM   AS langColumnNm
      FROM OP_BOARD_CONF_ARRANGE
     WHERE DOMAIN_CD = #{domainCd} 
       AND BBS_CD = #{bbsCd}
       AND LIST_VIEW_SE = #{listViewSe}
     ORDER BY IEM_ARNG_ORDR_NO ASC

mapper.insert:
/*_boardconf : boardconf-sqlmap.xml : boardConfArrangeInsert */
    INSERT INTO OP_BOARD_CONF_ARRANGE (
           DOMAIN_CD
         , BBS_CD
         , LIST_VIEW_SE
         , COLUMN_ID
         , COLUMN_NM
         , IEM_ARNG_ORDR_NO
         , BEAN_NM
         , LANG_COLUMN_NM
    ) 
    VALUES (
           #{domainCd}
         , #{bbsCd}
         , #{listViewSe}
         , #{columnId}
         , (SELECT columnNm FROM 
                   (SELECT COLUMN_NM AS columnNm FROM OP_BOARD_CONF_EXTENSION
                     WHERE DOMAIN_CD = #{domainCd} 
                       AND BBS_CD = #{bbsCd}
                       AND UPPER(COLUMN_ID) = #{columnId}
                       AND USE_YN = 'Y'
                     UNION
                    SELECT DESCRIPTION AS columnNm FROM _cub_schema_comments
                     WHERE TABLE_NAME = 'op_board'

                       AND UPPER(COLUMN_NAME) = #{columnId}
                   ) WHERE ROWNUM = 1)
         , #{arrangeOrdrNo}
         , #{beanNm}
         , #{langColumnNm}
    )

