mapper.select:
/* _explorer.goodsList */ SELECT ROWNUM NUM , X.* , TO_CHAR(exmntConfmDate,'YYYY.MM.DD HH24:mi') AS exmntConfmDt , TO_CHAR(ADDDATE(exmntConfmDate, 7),'YYYY.MM.DD HH24:mi') AS exmntLimitDt , DATEDIFF(SYSDATETIME, ADDDATE(exmntConfmDate, 7)) AS exmntDay , TO_CHAR(ADDDATE(exmntConfmDate, 3),'YYYY.MM.DD HH24:mi') AS confmLimitDt , DATEDIFF(SYSDATETIME, ADDDATE(exmntConfmDate, 3)) AS confmDay FROM (SELECT A.GOODS_CODE AS GOODS_CODE , A.LANG_CODE AS LANG_CODE , GOODS_NM AS goodsNm , GOODS_PC AS goodsPc , (SELECT LANG_NM FROM TCO_SVC_LANG_C WHERE LANG_CODE = A.LANG_CODE) AS langNm , A.GOODS_REGIST_STTUS AS goodsRegistSttus , (SELECT INDVDLZ_CD_NM FROM OP_CODE_INDVDLZ WHERE LANG_CODE = '00' AND GROUP_CD = 1008 AND INDVDLZ_CD = A.GOODS_REGIST_STTUS) AS goodsRegistSttusNm , USER_ID AS userId , TO_CHAR(REGIST_DT,'YYYY.MM.DD HH24:mi') AS registDt , (SELECT EXMNT_CONFM_DT FROM TST_EXMN_CNFM_H WHERE GOODS_CODE = A.GOODS_CODE AND EXMNT_CONFM_CD = A.GOODS_REGIST_STTUS ORDER BY EXMNT_CONFM_DT DESC LIMIT 1) AS exmntConfmDate , (SELECT LANG_STORE_NM FROM TUM_SVC_STOR_I WHERE USER_ID = A.USER_ID AND LANG_CODE = A.LANG_CODE) AS langStoreNm FROM TST_GOOD_INFO_I A WHERE MNGR_DELETE_AT = 'N' AND GOODS_ACTVTY_AT = 'Y'
ORDER BY exmntConfmDate NULLS LAST, REGIST_DT DESC
ORDER BY exmntConfmDate NULLS LAST, REGIST_DT DESC
ORDER BY CONFM_DT DESC NULLS LAST
) X WHERE ROWNUM <= 5

		/* explorer.goodsMarkImageList */
		SELECT B.MARK_IMAGE_FILE_SEQ AS MARKIMAGEFILESEQ
          FROM TST_GOOD_MARK_I A, TST_GOOD_MARK_M B 
         WHERE A.MARK_CD = B.MARK_CD
           AND A.GOODS_CODE = #{goodsCode}
         ORDER BY A.MARK_CD

/*explorer.goodsDelayCnt*/ SELECT COUNT(B.GOODS_CODE) FROM (SELECT A.GOODS_CODE , (SELECT EXMNT_CONFM_DT FROM TST_EXMN_CNFM_H WHERE GOODS_CODE = A.GOODS_CODE AND EXMNT_CONFM_CD = A.GOODS_REGIST_STTUS ORDER BY EXMNT_CONFM_DT DESC LIMIT 1) AS exmntConfmDate FROM TST_GOOD_INFO_I A WHERE MNGR_DELETE_AT = 'N' AND GOODS_ACTVTY_AT = 'Y'
) B WHERE 1 = 1
AND DATEDIFF(SYSDATETIME, ADDDATE(B.exmntConfmDate, 3)) >= 1
AND DATEDIFF(SYSDATETIME, ADDDATE(B.exmntConfmDate, 7)) >= 1

    /* explorer.cmmntyEsthCnt */
    	SELECT COUNT(b.CMMNTY_ID)
    	  FROM TCM_CMNT_INFO_I b
	         , OP_USER c
		 WHERE c.USER_ID = b.USER_ID
		   AND b.CMMNTY_STTUS_CD = #{cmmntySttusCd}

/*_explorer.cntrctServiceDetailCount*/ SELECT COUNT(1) AS totalCount FROM TCM_CNTRCT_MANAGE_I A LEFT JOIN TCM_CNTRCT_TEMP_USER C ON A.TEMP_USER_SN = C.USER_SN LEFT JOIN OP_USER_PBLINSTT_OPTION D ON A.COMM_USER_ID = D.USER_ID
WHERE A.DELETE_AT != 'N' AND A.ASIS = 'N'
AND A.GOODS_CODE = #{goodsCode}
AND A.user_id = #{userId}

/*_explorer.cntrctServiceDetailList*/ SELECT Y.* FROM ( SELECT ROWNUM NUM, X.* FROM ( SELECT A.CNTRCT_SN AS cntrctSn , A.USER_ID AS userId , A.SVC_NM AS svcNm , TO_CHAR(CNTR_DT,'yyyy-mm-dd') AS cntrDt , A.CNTRCT_BEGIN_DE AS cntrctBeginDe , A.CNTRCT_END_DE AS cntrctEndDe , A.CNTRCTAMOUNT AS cntrctamount , A.PURCHS_INSTT_NM AS purchsInsttNm , CASE WHEN A.goods_code IS NOT NULL THEN (SELECT B.cmpny_nm FROM tst_good_info_i C , op_user_entrprs_option B WHERE C.user_id = B.user_id AND C.goods_code = A.goods_code) ELSE A.SLE_INSTT_NM END AS sleInsttNm , A.CTRTC_SN AS ctrtcSn , IFNULL(A.COMM_USER_ID,A.REGIST_ID) AS registId , A.REGIST_DT AS registDt , A.UPDT_ID AS updtId , A.UPDT_DT AS updtDt , A.DELETE_AT AS deleteAt , A.TEMP_USER_SN AS tempUserSn , A.CNTR_COUNT AS cntrCount , B.GOODS_NM AS goodsNm , A.USE_AT AS useAt FROM TCM_CNTRCT_MANAGE_I A LEFT JOIN TST_GOOD_INFO_I B ON A.GOODS_CODE = B.GOODS_CODE LEFT JOIN TCM_CNTRCT_TEMP_USER C ON A.TEMP_USER_SN = C.USER_SN LEFT JOIN OP_USER_PBLINSTT_OPTION D ON A.COMM_USER_ID = D.USER_ID LEFT JOIN OP_CODE_INDVDLZ G ON G.GROUP_CD = '2046' AND G.INDVDLZ_CD = A.REGIST_MTHD_CODE
WHERE A.DELETE_AT != 'N' AND A.ASIS = 'N'
AND A.GOODS_CODE = #{goodsCode}
AND A.user_id = #{userId}
ORDER BY A.REGIST_DT DESC ) X WHERE ROWNUM <= #{pagingEndNum} ) Y WHERE 1=1
AND NUM >= #{pagingStartNum}

/*_explorer.cntrctUseInsttList*/ SELECT userTy ,userTyCd ,purchsInsttNm ,cntrctAmount ,cntrCount ,cntrctNum FROM ( SELECT DECODE(userTyCd, '1001', '개인', '2001', '기업', '3001', '공공') AS userTy ,userTyCd ,purchsInsttNm ,SUM(cntrctAmount) AS cntrctAmount ,SUM(NVL(cntrCount, 0)) AS cntrCount ,COUNT(*) AS cntrctNum FROM ( SELECT (SELECT DECODE(user_ty_cd, '1001', '1001', '1002', '1001', '2001', '2001', '2002', '2001', '3001', '3001', '3002', '3001') FROM op_user WHERE user_id = A.user_id ) AS userTyCd ,A.purchs_instt_nm AS purchsInsttNm ,A.cntrctamount AS cntrctAmount ,A.cntr_count AS cntrCount FROM tcm_cntrct_manage_i A
WHERE A.DELETE_AT != 'N' AND A.ASIS = 'N' AND TO_CHAR(A.cntr_dt, 'YYYY-MM') BETWEEN '2016-01' AND TO_CHAR(#{basicDate}, 'YYYY-MM') ) GROUP BY userTyCd ,purchsInsttNm ) WHERE 1=1
AND userTy = #{userTy}
AND purchsInsttNm LIKE '%' || #{purchsInsttNm} || '%'
ORDER BY cntrctAmount DESC
ORDER BY purchsInsttNm ASC

/*_explorer.contractList*/ SELECT Y.* FROM ( SELECT ROWNUM NUM, X.* FROM (
SELECT cntrctSn ,userId ,svcNm ,cntrDt ,cntrctBeginDe ,cntrctEndDe ,cntrctamount ,purchsInsttNm ,sleInsttNm ,ctrtcSn ,registId ,registDt ,updtId ,updtDt ,deleteAt ,tempUserSn ,cntrCount ,goodsNm ,useAt ,userTyCd FROM ( SELECT A.CNTRCT_SN AS cntrctSn ,A.USER_ID AS userId ,A.SVC_NM AS svcNm ,TO_CHAR(CNTR_DT,'yyyy-mm-dd') AS cntrDt ,A.CNTRCT_BEGIN_DE AS cntrctBeginDe ,A.CNTRCT_END_DE AS cntrctEndDe ,A.CNTRCTAMOUNT AS cntrctamount ,A.PURCHS_INSTT_NM AS purchsInsttNm ,CASE WHEN A.goods_code IS NOT NULL THEN (SELECT B.cmpny_nm FROM tst_good_info_i C ,op_user_entrprs_option B WHERE C.user_id = B.user_id AND C.goods_code = A.goods_code) ELSE A.SLE_INSTT_NM END AS sleInsttNm ,A.CTRTC_SN AS ctrtcSn ,IFNULL(A.COMM_USER_ID,A.REGIST_ID) AS registId ,A.REGIST_DT AS registDt ,A.UPDT_ID AS updtId ,A.UPDT_DT AS updtDt ,A.DELETE_AT AS deleteAt ,A.TEMP_USER_SN AS tempUserSn ,A.CNTR_COUNT AS cntrCount ,B.GOODS_NM AS goodsNm ,A.USE_AT AS useAt ,(SELECT DECODE(user_ty_cd, '1001', '1001', '1002', '1001', '2001', '2001', '2002', '2001', '3001', '3001', '3002', '3001') FROM OP_USER WHERE USER_ID = A.USER_ID ) AS userTyCd FROM TCM_CNTRCT_MANAGE_I A LEFT JOIN TST_GOOD_INFO_I B ON A.GOODS_CODE = B.GOODS_CODE LEFT JOIN TCM_CNTRCT_TEMP_USER C ON A.TEMP_USER_SN = C.USER_SN LEFT JOIN OP_USER_PBLINSTT_OPTION D ON A.COMM_USER_ID = D.USER_ID LEFT JOIN OP_CODE_INDVDLZ G ON G.GROUP_CD = '2046' AND G.INDVDLZ_CD = A.REGIST_MTHD_CODE
WHERE A.DELETE_AT != 'N' AND A.ASIS = 'N' AND TO_CHAR(A.cntr_dt, 'YYYY-MM') BETWEEN '2016-01' AND TO_CHAR(#{basicDate}, 'YYYY-MM') AND A.PURCHS_INSTT_NM = #{purchsInsttNm} ) WHERE usertycd = #{userTyCd} ORDER BY registDt DESC
) X WHERE ROWNUM <= #{pagingEndNum} ) Y WHERE NUM >= #{pagingStartNum}

/*_explorer.cntrctSportList*/ SELECT svcNm /*사업명*/ , goodsCode , NVL(goodsKnd, 'SaaS') AS goodsKnd /*서비스 구분*/ , sleInsttNm /*서비스 명 */ , goodsMakr /*판개기관*/ , purchsInsttNm /*계약기관*/ , cntrDt /*계약체곌일*/ , cntrctBeginDe /*계약기간 시작일*/ , cntrctEndDe /*계약기간 종료일*/ , cntrCount /*지원계약 수*/ , cntrctAmount /*주 계약금액*/ FROM (SELECT TRIM(A.svc_nm) AS svcNm , A.goods_code AS goodsCode , (SELECT DECODE(goods_knd_cd, 1001, 'PaaS', 1002, 'SaaS', 1003, 'IaaS') FROM tst_good_info_i WHERE goods_code = A.goods_code) AS goodsKnd , A.sle_instt_nm AS sleInsttNm , (SELECT goods_makr FROM tst_good_info_i WHERE goods_code = A.goods_code) AS goodsMakr , A.purchs_instt_nm AS purchsInsttNm , TO_CHAR(A.cntr_dt, 'YYYY-MM-DD') AS cntrDt , (SUBSTRING(A.cntrct_begin_de, 0, 4) || '-' || SUBSTRING(A.cntrct_begin_de, 5, 2) || '-' || SUBSTRING(A.cntrct_begin_de, 7, 2)) AS cntrctBeginDe , (SUBSTRING(A.cntrct_end_de, 0, 4) || '-' || SUBSTRING(A.cntrct_end_de, 5, 2) || '-' || SUBSTRING(A.cntrct_end_de, 7, 2)) AS cntrctEndDe , NVL(A.cntr_count, 0) AS cntrCount , NVL(A.cntrctamount, 0) AS cntrctAmount FROM tcm_cntrct_manage_i A
WHERE A.DELETE_AT != 'N' AND A.ASIS = 'N' AND A.SPORT_AT = 'Y' AND TO_CHAR(A.cntr_dt, 'YYYY-MM') BETWEEN '2016-01' AND TO_CHAR(#{basicDate}, 'YYYY-MM')
AND TO_CHAR(A.cntr_dt, 'YYYY') = #{cntrDt}
ORDER BY A.cntr_dt DESC )

/*_explorer.cntrctSportTotList*/ SELECT cntrDt , COUNT(svcNm) AS svcNmCnt , SUM(cntrCount) AS cntrCount , SUM(cntrctAmount) AS cntrctAmount FROM (SELECT TRIM(A.svc_nm) AS svcNm , (SELECT DECODE(goods_knd_cd, 1001, 'PaaS', 1002, 'SaaS', 1003, 'IaaS', 1004, 'SCS') FROM tst_good_info_i WHERE goods_code = A.goods_code) AS goodsKnd , A.sle_instt_nm AS sleInsttNm , (SELECT goods_makr FROM tst_good_info_i WHERE goods_code = A.goods_code) AS goodsMakr , A.purchs_instt_nm AS purchsInsttNm , TO_CHAR(A.cntr_dt, 'YYYY') AS cntrDt , TO_CHAR(A.cntrct_begin_de, 'YYYY-MM-DD') AS cntrctBeginDe , TO_CHAR(A.cntrct_end_de, 'YYYY-MM-DD') AS cntrctEndDe , NVL(A.cntr_count, 0) AS cntrCount , NVL(A.cntrctamount, 0) AS cntrctAmount FROM tcm_cntrct_manage_i A
WHERE A.DELETE_AT != 'N' AND A.ASIS = 'N' AND TO_CHAR(A.cntr_dt, 'YYYY-MM') BETWEEN '2016-01' AND TO_CHAR(#{basicDate}, 'YYYY-MM') ORDER BY A.cntr_dt DESC ) WHERE 1=1
AND TO_CHAR(cntrDt, 'YYYY') = #{cntrDt}
GROUP BY cntrDt
