mapper.select:
    	/* _bidInfo.bidInfoList */
		SELECT Y.* FROM (
	    	SELECT ROWNUM NUM, X.* FROM (
				SELECT 
				        FORN_GOODS_NM	fornGoodsNm  		
				       ,FORN_CMPNY_NM	fornCmpnyNm 		
				       ,FORN_GOODS_SUMRY fornGoodsSumry	 
				       ,FORN_KND_NM		fornKndNm		
				       ,FORN_NOTE		fornNote 		
				       ,FORN_LINK_URL	fornLinkUrl		
				       ,(SELECT TO_CHAR(MAX(UPDATE_DT),'YYYY-MM-DD') FROM TST_FORN_GOODS_I)  updateDt	
				  FROM  TST_FORN_GOODS_I 

			) X WHERE ROWNUM <= #{pagingEndNum}
		) Y WHERE NUM >= #{pagingStartNum}
